import React from 'react'
import './style.scss'

import { useEffect, useState } from 'react'
import { USER_MAIN_DATA } from '../../data/mocked-data'
import { Modelisation } from '../../data/Modelisation'
import { useParams } from 'react-router-dom'

const Energie = (props) => {
  const { image, name } = props
  const [data, setData] = useState(null)
  const paramsId = useParams()

  useEffect(() => {
    const modelisation = new Modelisation(USER_MAIN_DATA)
    setData(modelisation.formatDataEnergy(paramsId))
  }, [])

  console.log(data)

  console.log(name)
  let energyValueCalorie = data.calorieCount
  console.log(energyValueCalorie)
