import './style.scss'

import { USER_AVERAGE_SESSIONS } from '../../data/mocked-data'
import { Modelisation } from '../../data/Modelisation'
import useFetch from '../../hooks/useFetch'

// import React, { PureComponent } from 'react'
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  Rectangle,
} from 'recharts'

function DiagrammeCourbe({ userId }) {
  const { data, error } = useFetch(
    `http://localhost:3000/user/${userId}/average-sessions`,
    getUserDataMockWithId()
  )

  function getUserDataMockWithId() {
    const userData = USER_AVERAGE_SESSIONS.find(
      (user) => +user.userId === +userId
    )
    return userData
  }

  function getData() {
    const modelisation = new Modelisation(data)
    return modelisation.formatDataSessions()
  }

  if (data !== null) {
    const CustomTooltip = ({ active, payload }) => {
      if (active && payload && payload.length) {
        return (
          <div className="custom-tooltip-courbe">
            <p className="kg">{`${payload[0].value} min`}</p>
          </div>
        )
      }

      return null
    }
    return (
      <div className={`diagrammes-item diagrammes_diagramme-courbe`}>
        <p className="title">
          Dur√©e moyenne des
          <br />
          sessions
        </p>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart
            // width={300}
            data={getData()}
            margin={{
              top: 55,
              right: 12,
              left: 12,
              bottom: 5,
            }}
          >
            <Line
              type="natural"
              dataKey="pv"
              stroke="white"
              strokeWidth={1}
              fill="white"
              dot={false}
              activeDot={{
                stroke: 'rgba(255,255,255, 0.6)',
                strokeWidth: 8,
                r: 5,
              }}
            />

            <XAxis
              dataKey="name"
              stroke="rgba(255,255,255, 0.5)"
              strokeWidth={0}
              tick={{ fontSize: 12 }}
            />
            <YAxis hide padding={{ top: 70, bottom: 20 }} />

            <Tooltip
              content={<CustomTooltip />}
              wrapperStyle={{
                outline: 'none',
                backgroundColor: 'transparent',
              }}
              cursor={{
                stroke: 'rgba(0, 0, 0, 0.1)',
                strokeWidth: 80,
              }}
            />
            <Rectangle
              width={20}
              height={20}
              x={points[0].x}
              y={1}
              wrapperStyle={{ backgroundColor: 'blue' }}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
    )
  }
}

export default DiagrammeCourbe
