// import { USER_MAIN_DATA } from '../data/mocked-data'
// import { USER_ACTIVITY } from '../data/mocked-data'
// import { USER_AVERAGE_SESSIONS } from '../data/mocked-data'
// import { USER_PERFORMANCE } from '../data/mocked-data'

export class Modelisation {
  constructor(data) {
    this.data = data
  }

  //component Bonjour
  formatUserName() {
    console.log(this.data)
    console.log(this.data.userInfos)
    const dataFormated = this.data?.userInfos.map((item) => {
      return {
        name: item.firstName,
      }
    })

    return dataFormated
  }

  // composant Energie
  formatDataEnergy(params) {
    for (let element of this.data) {
      if (element.id.toString() === params.id) {
        return {
          calorieCount: element.keyData.calorieCount,
          proteinCount: element.keyData.proteinCount,
          carbohydrateCount: element.keyData.carbohydrateCount,
          lipidCount: element.keyData.lipidCount,
        }
      }
    }
  }
  // composant Diagramme Score
  formatDataScore(params) {
    for (let element of this.data) {
      if (element.id.toString() === params.id) {
        return {
          name: 'score',
          value: element.todayScore * 100,
        }
      }
    }
  }

  // composant Diagramme courbe
  formatDataSessions(params) {
    const data = []

    for (let element of this.data) {
      if (element.userId.toString() === params.id) {
        element.sessions.map((el) => {
          // convertit les nombres en jours
          switch (el.day) {
            case 1:
              el.day = 'L'
              break

            case 2:
              el.day = 'M'
              break
            case 3:
              el.day = 'M'
              break
            case 4:
              el.day = 'J'
              break
            case 5:
              el.day = 'V'
              break
            case 6:
              el.day = 'S'
              break
            case 7:
              el.day = 'D'
              break
            default:
            // console.log(`erreur de jour ${el.day}.`)
          }

          data.push({
            name: el.day,
            // uv: 0,
            pv: el.sessionLength,
            // amt: 0,
          })
          return data
        })
        return data
      }
    }
  }
  formatDataActivity() {
    const dataFormated = this.data?.sessions.map((item, index) => {
      return {
        name: index + 1,
        uv: item.calories,
        pv: item.kilogram,
      }
    })

    return dataFormated
  }

  // formatDataActivity() {
  //   console.log('dans classe de modelisation')
  //   const data = []
  //   let sliced

  //   const formatDate = (string) => {
  //     string.slice(-2) === '0'
  //       ? (sliced = string.slice(-2))
  //       : (sliced = string.slice(-1))
  //     return sliced
  //   }

  //   for (let element of this.data) {
  //     if (element.userId.toString() === params.id) {
  //       element.sessions.map((el) => {
  //         data.push({
  //           name: formatDate(el.day),
  //           uv: el.kilogram,
  //           pv: el.calories,
  //           // pvgraph: el.calories / 2.5,
  //         })
  //         return data
  //       })
  //       return data
  //     }
  //   }
  // }

  // composant diagramme toile
  formatDataRadarChart(params) {
    const data = []

    for (let element of this.data) {
      if (element.userId.toString() === params.id) {
        element.data.map((el) => {
          data.push({
            subject: element.kind[el.kind],
            A: el.value,
          })
          return data
        })
        return data
      }
    }
  }
}
