import React from 'react'
import './style.scss'

import { useEffect, useState } from 'react'
import { USER_MAIN_DATA } from '../../data/mocked-data'
import { Modelisation } from '../../data/Modelisation'
import { useParams } from 'react-router-dom'

const Energie = (props) => {
  const { image, name } = props
  const [data, setData] = useState(null)
  const paramsId = useParams()

  useEffect(() => {
    const modelisation = new Modelisation(USER_MAIN_DATA)
    setData(modelisation.formatDataEnergie(paramsId))
  }, [])

  console.log(data)

  console.log(name)

  switch (name) {
    case 'Oranges':
      console.log('Oranges are $0.59 a pound.')
      break
    case 'Mangoes':

    case 'Papayas':
      console.log('Mangoes and papayas are $2.79 a pound.')
      // expected output: "Mangoes and papayas are $2.79 a pound."
      break
    default:
      console.log(
        `Sorry, ${name} is not a correct prop name - choose Calories / Protéines / Glucides or Lipides.`
      )
  }

  return (
    <div className={`energie-item energie_${name}`}>
      <img src={image} alt={name} />
      <div>
        <h3>{'value'}</h3>
        <p>{name}</p>
      </div>
    </div>
  )
}

export default Energie
